import TinderCard from 'react-tinder-card'
import React, {useEffect, useState} from 'react'
import ChatContainer from '../components/ChatContainer'
import {useCookies} from 'react-cookie'
import axios from 'axios'
import Fireworks from '../components/Fireworks'

const Dashboard = () => {
    const [user, setUser] = useState(null)
    const [genderedUsers, setGenderedUsers] = useState(null)
    const [lastDirection, setLastDirection] = useState()
    const [cookies, setCookie, removeCookie] = useCookies(['user'])
    const containerStyle = {
        backgroundColor: '#F0F0F0'
      }
    const userId = cookies.UserId

    const [showSwipeInfo, setShowSwipeInfo] = useState(false)
    const [fireworksPosition, setFireworksPosition] = useState({ positionX: 0, positionY: 0 }); // Define fireworksPosition state

    const getUser = async () => {
        try {
            const response = await axios.get('http://localhost:8000/user', {
                params: {userId}
            })
            setUser(response.data)
        } catch (error) {
            console.log(error)
        }
    }

    const getGenderedUsers = async () => {
        try {
            const response = await axios.get('http://localhost:8000/gendered-users', {
                params: {gender: user?.gender_interest}
            })
            setGenderedUsers(response.data)
        } catch (error) {
            console.log(error)
        }
    }

    useEffect(() => {
        getUser()

    }, [])

    useEffect(() => {
        if (user) {
            getGenderedUsers()
        }
    }, [user])

    const updateMatches = async (matchedUserId) => {
        try {
            await axios.put('http://localhost:8000/addmatch', {
                userId,
                matchedUserId
            })
            getUser()
            const swipeInfoElement = document.querySelector('.swipe-info');
            if (swipeInfoElement) {
              const rect = swipeInfoElement.getBoundingClientRect();
              const fireworksPositionX = rect.right + 40;
              const fireworksPositionY = rect.top + (rect.height / 2);

              // Delay the appearance of the swipe info and fireworks
              setShowSwipeInfo(true);
              setFireworksPosition({ positionX: fireworksPositionX, positionY: fireworksPositionY });

              // Hide the swipe info after a delay
              const hideTimer = setTimeout(() => {
                setShowSwipeInfo(false);
                setLastDirection('');
              }, 2000);

              return () => clearTimeout(hideTimer);
            }
            setLastDirection('right')
        } catch (err) {
            console.log(err)
        }
    }

    useEffect(() => {
        if (showSwipeInfo) {
            const timer = setTimeout(() => {
                setShowSwipeInfo(false);
                setLastDirection('');
            }, 2000);

            return () => clearTimeout(timer);
        }
    }, [showSwipeInfo]);

    const swiped = (direction, swipedUserId) => {
        if (direction === 'right') {
          updateMatches(swipedUserId);
          setLastDirection('right');
        }
      };

      const handleSwipeRight = (positionX, positionY) => {
        setFireworksPosition({ positionX, positionY }); // Update fireworksPosition state
        setShowSwipeInfo(true);
        const timer = setTimeout(() => {
          setShowSwipeInfo(false);
          setLastDirection('');
        }, 2000)
        return () => clearTimeout(timer);
      }

    const outOfFrame = (name) => {
        console.log(name + ' left the screen!')
    }

    const deg2rad = (deg) => deg * (Math.PI / 180);

    const matchedUserIds = user?.matches.map(({user_id}) => user_id).concat(userId)

    const filteredGenderedUsers = genderedUsers?.filter(genderedUser => !matchedUserIds.includes(genderedUser.user_id))


    console.log('filteredGenderedUsers ', filteredGenderedUsers)
    return (
        <div style={containerStyle}>
            {user &&
            <div className="dashboard">
                <ChatContainer user={user}/>
                <div className="swipe-container">
                    <div className="card-container">
                        {filteredGenderedUsers?.map((genderedUser) =>
                            <TinderCard
                                className="swipe"
                                key={genderedUser.user_id}
                                onSwipe={(dir) => swiped(dir, genderedUser.user_id)}
                                onCardLeftScreen={() => outOfFrame(genderedUser.first_name)}
                                onSwipeRight={(event) =>
                                    handleSwipeRight(
                                    event.clientX - event.target.getBoundingClientRect().left,
                                    event.clientY - event.target.getBoundingClientRect().top
                                    )
                                }
                            >
                                <div
                                    style={{backgroundImage: "url(" + genderedUser.url + ")", position: 'relative'}}
                                    className="card">
                                    {showSwipeInfo && (
                                    <div className="swipe-info">
                                        <Fireworks show={showSwipeInfo} positionX={fireworksPosition.positionX} positionY={fireworksPosition.positionY} />
                                        <div>
                                        <p>You Swiped Right</p>
                                        </div>
                                    </div>
                                    )}                                    

                                    <div className = "info-board">
                                        <h3 style={{display: 'inline-block', marginLeft: '10px', marginTop: '5px', marginBottom: '-5px', fontWeight: 'bold', fontSize: '37px', background: '-webkit-linear-gradient(right, #ce672f, #fe7d9b', WebkitBackgroundClip: 'text', color: 'transparent'}}>{genderedUser.first_name} {genderedUser.last_name}</h3>
                                        <h3 style={{display: 'inline-block', marginLeft: '10px', marginTop: '5px', marginBottom: '-5px', fontSize: '23px'}}>
                                            {(() => {
                                                const dob = new Date(genderedUser.dob_year, genderedUser.dob_month - 1, genderedUser.dob_day);
                                                const ageDiffMs = Date.now() - dob.getTime();
                                                const ageDate = new Date(ageDiffMs);
                                                return Math.abs(ageDate.getUTCFullYear() - 1970); // 
                                            })()}
                                        </h3>
                                        <h3 style={{ marginLeft: '10px', marginBottom: '-5px' }}>{genderedUser.about}</h3>
                                        <h3 style={{ marginLeft: '10px', marginBottom: '10px' }}>
                                            {(() => {
                                                const R = 6371; // Radius of the Earth in kilometers
                                                const dLat = deg2rad(genderedUser.latitude - user.latitude);
                                                const dLon = deg2rad(genderedUser.longitude - user.longitude);

                                                const a =
                                                Math.sin(dLat / 2) * Math.sin(dLat / 2) +
                                                Math.cos(deg2rad(user.latitude)) * Math.cos(deg2rad(genderedUser.latitude)) * Math.sin(dLon / 2) * Math.sin(dLon / 2);

                                                const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
                                                const distance = R * c; // Distance in kilometers

                                                return distance.toFixed(2); // Adjust as needed, rounding to two decimal places
                                            })()} km away
                                        </h3>
                                    </div>
                                </div>
                            </TinderCard>
                        )}
                    </div>
                </div>
            </div>}
        </div>
    )
}
export default Dashboard
