import React, { useEffect, useRef } from 'react';

const Fireworks = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const particles = [];

    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    function Particle() {
      this.x = canvas.width / 2;
      this.y = canvas.height / 2;
      this.color = getRandomColor();
      this.size = Math.random() * 10 + 5; // Kích thước của hạt pháo hoa từ 5 đến 15
      this.velocityX = Math.random() * 6 - 3; // Tốc độ di chuyển theo trục X từ -3 đến 3
      this.velocityY = Math.random() * -6 - 3; // Tốc độ di chuyển theo trục Y từ -3 đến -9
      this.opacity = 1;
      this.fadeOut = 0.02;
      this.shape = Math.random() < 0.5 ? 'circle' : 'square'; // Chọn ngẫu nhiên hình dạng là hình tròn hoặc hình vuông
    }

    Particle.prototype.draw = function() {
      ctx.globalCompositeOperation = 'lighter';
      ctx.beginPath();
      if (this.shape === 'circle') {
        ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
      } else if (this.shape === 'square') {
        ctx.rect(this.x - this.size / 2, this.y - this.size / 2, this.size, this.size);
      }
      ctx.closePath();
      ctx.fillStyle = this.color;
      ctx.fill();
    };

    Particle.prototype.update = function() {
      this.x += this.velocityX;
      this.y += this.velocityY;
      this.opacity -= this.fadeOut;
    };

    function createParticles() {
      for (let i = 0; i < 50; i++) {
        const particle = new Particle();
        particles.push(particle);
      }
    }

    function animate() {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      particles.forEach((particle, index) => {
        particle.draw();
        particle.update();
        if (particle.opacity <= 0) {
          particles.splice(index, 1);
        }
      });

      requestAnimationFrame(animate);
    }

    function getRandomColor() {
      const colors = ['#fe8e7d', '#fe7d9b', '#fe9bce', '#ff99cc', '#ff6699']; // Mảng màu tương tự
      return colors[Math.floor(Math.random() * colors.length)];
    }

    createParticles();
    animate();
  }, []);

  return <canvas ref={canvasRef} style={{ position: 'absolute', top: 0, left: 0 }} />;
};

export default Fireworks;
