import React, { useEffect, useRef } from 'react';

const Fireworks = ({ positionX, positionY }) => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const particles = [];

    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    function Particle() {
      this.x = positionX;
      this.y = positionY;
      this.color = getRandomColor(); // Lấy màu sắc ngẫu nhiên
      this.radius = Math.random() * 2 + 1;
      this.velocityX = Math.random() * 6 - 3;
      this.velocityY = Math.random() * 6 - 3;
      this.opacity = 1;
      this.fadeOut = 0.02;
    }

    Particle.prototype.draw = function() {
      ctx.globalCompositeOperation = 'lighter';
      ctx.beginPath();
      ctx.arc(this.x, this.y, this.radius, 0, Math.PI * 2);
      ctx.closePath();
      ctx.fillStyle = this.color;
      ctx.fill();
    };

    Particle.prototype.update = function() {
      this.x += this.velocityX;
      this.y += this.velocityY;
      this.opacity -= this.fadeOut;
    };

    function createParticles() {
      for (let i = 0; i < 30; i++) {
        const particle = new Particle();
        particles.push(particle);
      }
    }

    function animate() {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      particles.forEach((particle, index) => {
        particle.draw();
        particle.update();
        if (particle.opacity <= 0) {
          particles.splice(index, 1);
        }
      });

      requestAnimationFrame(animate);
    }

    function getRandomColor() {
      const colors = ['#FF0000', '#00FF00', '#0000FF', '#FFFF00', '#00FFFF']; // Mảng màu sắc
      return colors[Math.floor(Math.random() * colors.length)]; // Trả về một màu sắc ngẫu nhiên từ mảng
    }

    createParticles();
    animate();
  }, [positionX, positionY]);

  return <canvas ref={canvasRef} style={{ position: 'absolute', top: 0, left: 0 }} />;
};

export default Fireworks;
