import React, { useEffect, useRef } from 'react';

const Fireworks = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const particles = [];

    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    function Particle() {
      this.x = canvas.width;
      this.y = Math.random() * canvas.height;
      this.color = getRandomColor();
      this.size = Math.random() * 20 + 10; // Kích thước của trái tim từ 10 đến 30
      this.velocityX = Math.random() * -5 - 1; // Đổi hướng vận tốc X để di chuyển về bên phải
      this.velocityY = Math.random() * 10 - 5;
      this.opacity = 1;
      this.fadeOut = 0.02;
    }

    Particle.prototype.draw = function() {
      ctx.globalCompositeOperation = 'lighter';
      ctx.beginPath();
      ctx.moveTo(this.x, this.y + this.size / 4);
      ctx.lineTo(this.x + this.size / 2, this.y - this.size / 2);
      ctx.arc(this.x, this.y - this.size / 2, this.size / 4, 0, Math.PI);
      ctx.lineTo(this.x - this.size / 2, this.y - this.size / 2);
      ctx.arc(this.x, this.y - this.size / 2, this.size / 4, Math.PI, 0, true);
      ctx.closePath();
      ctx.fillStyle = this.color;
      ctx.fill();
    };

    Particle.prototype.update = function() {
      this.x += this.velocityX;
      this.y += this.velocityY;
      this.opacity -= this.fadeOut;
    };

    function createParticles() {
      for (let i = 0; i < 100; i++) {
        const particle = new Particle();
        particles.push(particle);
      }
    }

    function animate() {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      particles.forEach((particle, index) => {
        particle.draw();
        particle.update();
        if (particle.opacity <= 0 || particle.x < 0) { // Xóa particle khi mất khỏi màn hình hoặc mất opacity
          particles.splice(index, 1);
        }
      });

      requestAnimationFrame(animate);
    }

    function getRandomColor() {
      const colors = ['#fe8e7d', '#fe7d9b', '#fe9bce', '#ff99cc', '#ff6699']; // Mảng màu tương tự
      return colors[Math.floor(Math.random() * colors.length)];
    }

    createParticles();
    animate();
  }, []);

  return <canvas ref={canvasRef} style={{ position: 'absolute', top: 0, left: 0 }} />;
};

export default Fireworks;
