import React, { useEffect, useRef } from 'react';

const Fireworks = ({ positionX, positionY }) => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const particles = [];

    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    function Particle() {
      this.x = positionX;
      this.y = positionY;
      this.color = '#FFFFFF';
      this.radius = Math.random() * 2 + 1;
      this.velocityX = Math.random() * 6 - 3;
      this.velocityY = Math.random() * 6 - 3;
      this.opacity = 1;
      this.fadeOut = 0.02;
    }

    Particle.prototype.draw = function() {
      ctx.globalCompositeOperation = 'lighter';
      ctx.beginPath();
      ctx.arc(this.x, this.y, this.radius, 0, Math.PI * 2);
      ctx.closePath();
      ctx.fillStyle = this.color;
      ctx.fill();
    };

    Particle.prototype.update = function() {
      this.x += this.velocityX;
      this.y += this.velocityY;
      this.opacity -= this.fadeOut;
    };

    function createParticles() {
      for (let i = 0; i < 30; i++) {
        const particle = new Particle();
        particles.push(particle);
      }
    }

    function animate() {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      particles.forEach((particle, index) => {
        particle.draw();
        particle.update();
        if (particle.opacity <= 0) {
          particles.splice(index, 1);
        }
      });

      requestAnimationFrame(animate);
    }

    createParticles();
    animate();
  }, [positionX, positionY]);

  return (
    <div style={{ position: 'absolute', top: positionY, left: positionX, transform: 'translate(-50%, -50%)', textAlign: 'center' }}>
      <div style={{ position: 'relative', display: 'inline-block' }}>
        {/* Your fireworks animation logic */}
      </div>
      <p style={{ color: 'white', fontSize: '24px', position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)' }}>You Swiped Right</p>
    </div>
  )};

export default Fireworks;
